function cmap=sgcolormap(cmapname,n)
% calls sgKIM's colormap (case-insensitive)
%
% GKR GR_hi GR_low CP CKM BWR BCWYR BCWYR256 BCWYR-soft COLD256 wJET256 FNI256
% PAULA GRAY-BLUE-RED-BRIGHT GRAY-HSV-N LRY LBC DG-BLUE-RED
% RY BC BY HSV-N HSV-soft HSV-GM GRAY-RED GRAY-BLUE
% WR WB WY WG WC WM WRb BW-BLUE-RED_LIGHT
% BLUE-RED GRAY-BLUE-RED GW-BLUE-RED DRY DRY-L TONES18
% MBG darkautumn darksummer purpleOrange
% LVL_4X4 COSINE matlabcolororder_rainbow matlabcolororder_rgbcmyk
% matlabcolororder
%
% (cc) 2010-2019. solleo@gmail.com

if nargin==0
  help sgcolormap
  return
end
if ~exist('n','var'), n=256; end

switch upper(cmapname)
  case {'CBRY'} % cyan-blue-red-yellow
    n2 = floor(n/2);
    cmap = [[linspace(0,0,n2); linspace(1,0.25,n2); linspace(1,1,n2)]';
      [linspace(1,1,n2); linspace(0.25,1,n2); linspace(0,0,n2)]'];
  
  case {upper('matlabcolororder'),'MATCOLORORDER','MCO'}
    cmap=[
      0    0.4470    0.7410
      0.8500    0.3250    0.0980
      0.9290    0.6940    0.1250
      0.4940    0.1840    0.5560
      0.4660    0.6740    0.1880
      0.3010    0.7450    0.9330
      0.6350    0.0780    0.1840
      ];
  case {upper('matlabcolororder_rgbcmyk'),'MATCOLORORDER_RGB'}
    cmap = sgcolormap('matlabcolororder');
    cmap = cmap([2 5 1 6 4 3 7],:);
    
  case {upper('matlabcolororder_rainbow'),'MATCOLORORDER_RAINBOW'}
    cmap = sgcolormap('matlabcolororder');
    cmap = cmap([2 3 5 6 1 4 7],:);
  
  case {'COSINE'}
    t=linspace(0,1,n);
    cmap=0.5+0.5*[cos(2*pi*t); cos(2*pi*t-1/3*2*pi); cos(2*pi*t-2/3*2*pi)]';
    
  case {'LVL_4X4'}
    [l1,l2]=meshgrid(1:4,1:4);
    l1=reshape(l1(~eye(4)),[],1);
    l2=reshape(l2(~eye(4)),[],1);
    cmap=[l2./4 l1./4 abs(l1-l2)./3].*0.9;
    cmap=[cmap; .5 .5 .5];
    
  case {'PURPLEORANGE'}
    a=[109 1 175]/255;
    b=[255 168 122]/255;
    cmap=[linspace(a(1),b(1),n);linspace(a(2),b(2),n);linspace(a(3),b(3),n)]';
    
  case {'DARKAUTUMN'}
    a=[.85 0 0];
    b=[.95 .95 0];
    cmap=[linspace(a(1),b(1),n);linspace(a(2),b(2),n);linspace(a(3),b(3),n)]';
    cmap=flipud(cmap);
    
  case {'DARKSPRING'}
    a=[.9  0 .5];
    b=[1 .95 0];
    cmap=[linspace(a(1),b(1),n);linspace(a(2),b(2),n);linspace(a(3),b(3),n)]';
    
  case {'DARKSUMMER'}
    a=[1 1 0.5];
    b=[0 .3 .1];
    cmap=[linspace(a(1),b(1),n);linspace(a(2),b(2),n);linspace(a(3),b(3),n)]';
    
  case {'DG-AW'}
    n6 = round(n/6);
    cmap = [ones(n6,3)*.2 ; ones(n6+1,3)*.4; ...
      winter(round(n/3)); autumn(round(n/3))];
    
%   case {'DG-PARULA-INTERP'}
%     n6 = round(n/6);
%     cmap=[ ones(n6,3)*.2 ; ones(n6+1,3)*.4;
%       parula(n6*2*2)
%       ];
% 
%   case {'DG-JET-INTERP'}
%     n6 = round(n/6);
%     cmap=[ ones(n6,3)*.8 ; ones(n6+1,3)*1;
%       jet(n6*2*2)
%       ];
    
  case {'DG-PARULA'}
    n6 = round(n/6);
    cmap=[ ones(n6,3)*.2 ; ones(n6,3)*.4;
      parula(n6*2*2)
      ];
    
  case {'BCWYR-SOFT'}
    cmap=sgcolormap('BCWYR256');
    cmap=cmap((1+48):(256-48),:);
    
  case {'LRY'}
    cmap=[linspace(1,1,64); linspace(0.25,1,64); linspace(0,0,64)]';
    
  case {'LBC'}
    cmap=[linspace(0,0,64); linspace(0.25,1,64); linspace(1,1,64)]';
    
  case {'DG-BLUE-RED-BRIGHT'}
    cmap=[ ones(32,3)*.2 ; ones(32+1,3)*.4;
      sgcolormap('BC');
      sgcolormap('LRY')
      ];
    
  case {'GRAY-BLUE-RED-BRIGHT'}
    cmap=[ gray(64);
      sgcolormap('LBC');
      sgcolormap('LRY')
      ];
    
  case {'GRAY-HSV-N'}
    cmap=[ gray(64);
      sgcolormap('DBC');
      sgcolormap('DRY')
      ];
    
  case {'DG-BLUE-RED-LIGHT'} % dark gray - blue - red
    n6 = round(n/6);
    cmap=[ ones(n6,3)*.2 ; ones(n6,3)*.4;
      sgcolormap('LBC');
      sgcolormap('LRY');
      ];
    
  case {'DG-BLUE-RED'} % dark gray - blue - red
    n6 = round(n/6);
    cmap=[ ones(n6,3)*.2 ; ones(n6,3)*.4;
      sgcolormap('DBC');
      sgcolormap('DRY');
      ];
    
  case {'DG-BLUE-RED2'} % dark gray - blue - red
    n6 = round(n/6);
    cmap=[ ones(n6,3)*.15 ; ones(n6,3)*.35;
      sgcolormap('DBC2');
      sgcolormap('DRY');
      ];
    
%   case {'DG-BLUE-RED2-INTERP'} % dark gray - blue - red
%     n6 = round(n/6);
%     cmap=[ ones(n6,3)*.15 ; ones(n6+1,3)*.35;
%       sgcolormap('DBC2');
%       sgcolormap('DRY');
%       ];
    
  case {'DG-BLUE-RED3'} % dark gray - blue - red
    n6 = round(n/6);
    cmap=[ ones(n6,3)*.10 ; ones(n6,3)*.30;
      sgcolormap('DBC2',round(n/3));
      sgcolormap('DRY',round(n/3));
      ];
    
  case {'TONES18'}
    cmap=[.9 .9 .9; .8 .8 .8; flipud(jet(18))];
    
  case {'DRY-L'}
    cmap=hot(128);
    cmap=[1 1 1; cmap(17:113,:)];
    
  case {'BW-BLUE-RED_LIGHT'}
    cmap=[ ones(32,3)*.3 ; ones(32,3)*0.5;
      sgcolormap('BC');
      sgcolormap('RY');
      ];
    
  case {'DBC3'}
    cmap = [
      [linspace(0,0,n/2)', linspace(0,0.5,n/2)', linspace(0.8,1,n/2)'];   %  1~14 [14] blue
      [linspace(0,0,n/2)', linspace(0.5,1,n/2)', linspace(1,1,n/2)'];  % 15~44 [30] blue to cyan
      ];
    
  case {'DBC2'}
    cmap = [
      [linspace(0,0,n/2)', linspace(0,0.3,n/2)', linspace(0.8,1,n/2)'];   %  1~14 [14] blue
      [linspace(0,0,n/2)', linspace(0.3,1,n/2)', linspace(1,1,n/2)'];  % 15~44 [30] blue to cyan
      ];
    
  case {'DBC'}
    cmap = [
      [linspace(0,0,n/2)', linspace(0,0,n/2)', linspace(0.5667,1,n/2)'];   %  1~14 [14] dark blue to blue
      [linspace(0,0,n/2)', linspace(0.03333,1,n/2)', linspace(1,1,n/2)'];  % 15~44 [30] blue to cyan
      ];
    
  case {'DRY'} % for unsigned values
    cmap = [
      [linspace(0.5667,1,n/2)', linspace(0,0,n/2)', linspace(0,0,n/2)'];   %  1~14 [14] dark red to red
      [linspace(1,1,n/2)', linspace(0.03333,1,n/2)', linspace(0,0,n/2)'];  % 15~44 [30] red to yellow
      ];
    
  case {'GW-BLUE-RED'}
    cmap=[ ones(32,3)*.8 ; ones(32,3)*1;
      sgcolormap('DBC');
      sgcolormap('DRY');
      ];
    
  case {'BW-BLUE-RED'}
    cmap=[ ones(32,3)*.3 ; ones(32,3)*0.5;
      sgcolormap('DBC');
      sgcolormap('DRY')
      ];
    
  case {'GRAY-BLUE-RED','GRAY+BLUE+RED'}
    cmap=[ gray(64);
      sgcolormap('DBC');
      sgcolormap('DRY')
      ];
    
  case {'GRAY-BLUE-RED-LIGHT','GRAY+BLUE+RED-LIGHT'}
    cmap=[ gray(64);
      sgcolormap('BC');
      sgcolormap('RY')
      ];
    
  case {'BLUE-RED', 'BLUE+RED'}
    cmap=[
      flipud([      [linspace(0,0,14)', linspace(0,0,14)', linspace(0.5667,1,14)'];        %  1~14 [14] dark blue
      [linspace(0,0,30)', linspace(0.03333,1,30)', linspace(1,1,30)'];  % 15~44 [30] blue to cyan
      [linspace(0.05,1,20)', linspace(1,1,20)', linspace(1,1,20)']           % 45~64 cyan
      ]);
      [linspace(0.5667,1,14)', linspace(0,0,14)', linspace(0,0,14)'];        %  1~14 [14] dark red
      [linspace(1,1,30)', linspace(0.03333,1,30)', linspace(0,0,30)'];  % 15~44 [30] red to yellow
      [linspace(1,1,20)', linspace(1,1,20)', linspace(0.05,1,20)']           % 45~64 yellow to white
      ];
    
  case {'GRAY-RED', 'GRAY+RED'}
