    cmap=[linspace(0, 1, 256)', linspace(0.5, 0.5, 256)',linspace(1, 0, 256)'];
    % Blue to Yellow
  case 'BY1'
    %[.8 .8 .2; .2 .2 .8]
    cmap=[linspace(1, 0, 256)', linspace(1, 0, 256)',linspace(0, 1, 256)'];
    % Blue to Yellow
  case 'BY'
    %[.8 .8 .2; .2 .2 .8]
    cmap=[linspace(.8,.2,256)', linspace(.8,.2,256)',linspace(.2,.8,256)'];
    % Red to Yellow
  case 'RY'
    cmap=[1 0 0;1 0.0158730167895555 0;1 0.0317460335791111 0;1 0.0476190485060215 0;1 0.0634920671582222 0;1 0.0793650820851326 0;1 0.095238097012043 0;1 0.111111111938953 0;1 0.126984134316444 0;1 0.142857149243355 0;1 0.158730164170265 0;1 0.174603179097176 0;1 0.190476194024086 0;1 0.206349208950996 0;1 0.222222223877907 0;1 0.238095238804817 0;1 0.253968268632889 0;1 0.269841283559799 0;1 0.28571429848671 0;1 0.30158731341362 0;1 0.31746032834053 0;1 0.333333343267441 0;1 0.349206358194351 0;1 0.365079373121262 0;1 0.380952388048172 0;1 0.396825402975082 0;1 0.412698417901993 0;1 0.428571432828903 0;1 0.444444447755814 0;1 0.460317462682724 0;1 0.476190477609634 0;1 0.492063492536545 0;1 0.507936537265778 0;1 0.523809552192688 0;1 0.539682567119598 0;1 0.555555582046509 0;1 0.571428596973419 0;1 0.58730161190033 0;1 0.60317462682724 0;1 0.61904764175415 0;1 0.634920656681061 0;1 0.650793671607971 0;1 0.666666686534882 0;1 0.682539701461792 0;1 0.698412716388702 0;1 0.714285731315613 0;1 0.730158746242523 0;1 0.746031761169434 0;1 0.761904776096344 0;1 0.777777791023254 0;1 0.793650805950165 0;1 0.809523820877075 0;1 0.825396835803986 0;1 0.841269850730896 0;1 0.857142865657806 0;1 0.873015880584717 0;1 0.888888895511627 0;1 0.904761910438538 0;1 0.920634925365448 0;1 0.936507940292358 0;1 0.952380955219269 0;1 0.968253970146179 0;1 0.98412698507309 0;1 1 0];
